// 暴力匹配 func strStr(haystack string, needle string) int { if len(haystack) == 0 { return 0 } n := len(haystack) m := len(needle) if n &lt; m { return -1 } for i := 0; i &lt; n-m+1; i++ { flag := true for j := 0; i + j &lt; n &amp;&amp; j &lt; m; j++ { if haystack[i+j] != needle[j] { flag = false break } } if flag { return i } } return -1 } KMP算法：
