func reverseWords(s string) string { //1.使用双指针删除冗余的空格 slowIndex, fastIndex := 0, 0 b := []byte(s) //删除头部冗余空格 for len(b) &gt; 0 &amp;&amp; fastIndex &lt; len(b) &amp;&amp; b[fastIndex] == &#39; &#39; { fastIndex++ } //删除单词间冗余空格 for ; fastIndex &lt; len(b); fastIndex++ { if fastIndex-1 &gt; 0 &amp;&amp; b[fastIndex-1] == b[fastIndex] &amp;&amp; b[fastIndex] == &#39; &#39; { continue } b[slowIndex] = b[fastIndex] slowIndex++ } //删除尾部冗余空格 if slowIndex-1 &gt; 0 &amp;&amp; b[slowIndex-1] == &#39; &#39; { b = b[:slowIndex-1] } else { b = b[:slowIndex] } //2.反转整个字符串 reverse(&amp;b, 0, len(b)-1) //3.反转单个单词 i单词开始位置，j单词结束位置 i := 0 for i &lt; len(b) { j := i for ; j &lt; len(b) &amp;&amp; b[j] != &#39; &#39;; j++ { } reverse(&amp;b, i, j-1) i = j i++ } return string(b) } func reverse(b *[]byte, left, right int) { for left &lt; right { (*b)[left], (*b)[right] = (*b)[right], (*b)[left] left++ right-- } } 题目：剑指Offer58-II.左旋转字符串 力扣题目链接
